/* OpenGL Shading Language (GLSL) */

declare module '*.glsl' {
	const contents: string
	export = contents
}

declare module '*.vert' {
	const contents: string
	export = contents
}

declare module '*.frag' {
	const contents: string
	export = contents
}

/* WebGPU Shading Language (WGSL) */

declare module '*.wgsl' {
	const contents: string
	export = contents
}
